sys_variable_value.value:
try {
  (function execute(inputs, outputs) {
var instance = (inputs.instanceurl);
var role = (inputs.role);
var instancecountry = (inputs.instancecountry);
var httpResponseBody = (inputs.response);
var newTable = new GlideRecord('x_snc_coe_global_r_coe_global_users');
//newTable.initialize();

var parsed = new global.JSON.parse(httpResponseBody);
//var user = parsed.result[0].user;
//gs.info(username);
for (var i = 0; i < parsed.result.length; i++) {
              //  gs.info('id: ' + parsed.result[i].user.value);
  				 newTable.initialize();
                // newTable.query();
  				 newTable.roles = role;
  				 newTable.instance = instance;
  				 newTable.country = instancecountry;
  				 newTable.usersysid = parsed.result[i].user.value;
  				 newTable.username = parsed.result[i]['user.user_name'];
  			     newTable.firstname = parsed.result[i]['user.first_name'];
                 newTable.lastname = parsed.result[i]['user.last_name'];
                 newTable.lastlogin = parsed.result[i]['user.last_login_time'];
  			     newTable.active = parsed.result[i]['user.active'];
                // newTable.insert();
  				 var username = parsed.result[i]['user.user_name'];
   				 var usersysid = parsed.result[i].user.value;
  				 var firstname = parsed.result[i]['user.first_name'];
  				 var lastname = parsed.result[i]['user.last_name'];
  				 var lastlogin = parsed.result[i]['user.last_login_time'];
  				 var active = parsed.result[i]['user.active'];
  				 var sysId = newTable.insert();
				 //gs.info("Inserted record is " + sysId + " username parsed is " + username + " userSysId is "+ usersysid + "role is " + role + "firstname is: " + firstname + " Lastname is: " + lastname + "Last Login is: " + lastlogin + "active: " + active + " instance is: " + instance + " country is: " + instancecountry);
            }
})(inputs, outputs);
}
catch(err) {
  (function execute(inputs, outputs){
  var role = (inputs.role);
  gs.info(err.name);
  gs.info(err.message);
  gs.info(err.stack);
  gs.info("Error Processing this role: " + role);
  })(inputs, outputs);
}

