sys_variable_value.value:
try {
  (function execute(inputs, outputs) {
var role = (inputs.role);
var httpResponseBody = (inputs.response);
var parsed = new global.JSON.parse(httpResponseBody);
var alltitles = '';
for (var i = 0; i < parsed.result.length; i++) {
              //  gs.info('id: ' + parsed.result[i].user.value);


                // newTable.insert();
  				// var title = parsed.result[i]['user.user_name'];
   				// var usersysid = parsed.result[i].user.value;
   				var title = parsed.result[i].title;
   				//gs.info(title);
   				if(i == 0) alltitles += title
   				else alltitles += ", " + title;

            }
    outputs.apps = alltitles;        
    //gs.info(alltitles);

})(inputs, outputs);
}
catch(err) {
  (function execute(inputs, outputs){
  var role = (inputs.role);
  gs.info(err.name);
  gs.info(err.message);
  gs.info(err.stack);
  gs.info("Error Processing Application for: " + role);
  })(inputs, outputs);
}

