sys_variable_value.value:
try {
(function parse(inputs, outputs) {

    // Source item string is available in inputs.sourceItem.
    // Generate output by setting fields of outputs.targetObject.
    // 
    // The structure of outputs.targetObject corresponds to the
    // structure defined in the Data Stream Action output.
    // 
    // Optionally skip an item in the stream by setting
    // outputs.state = 'SKIP'
    //
    // JSON Example:
    // var item = JSON.parse(inputs.sourceItem);
    // outputs.targetObject.ffName = item.firstName;
    // outputs.targetObject.llName = item.lastName;
    // outputs.targetObject.address.home.city = item.address.home.city;

    var item = JSON.parse(inputs.sourceItem);
    outputs.targetObject.usersysid = item['user.value'];
    outputs.targetObject.username = item['user.user_name'];
    outputs.targetObject.firstname = item['user.first_name'];
    outputs.targetObject.lastName = item['user.last_name'];
    outputs.targetObject.lastlogin = item['user.last_login_time'];
    outputs.targetObject.active = item['user.active'];
    
    //
    // XML Example: 
    // var xmlDoc = new XMLDocument2();
    // xmlDoc.parseXML(inputs.sourceItem);
    // var root = xmlDoc.getDocumentElement();
    // if (root.getNodeName() != 'user') {
    //     outputs.state = 'SKIP';
    //     return;
    // }
    // outputs.targetObject.id = xmlDoc.getNodeText('/user/id');
    // outputs.targetObject.fname = xmlDoc.getNodeText('/user/person/first_name');
    // outputs.targetObject.lname = xmlDoc.getNodeText('/user/person/last_name');
    
})(inputs, outputs);
}
catch(err) {
  (function execute(inputs, outputs){
  var item = JSON.parse(inputs.sourceItem);
  var user = item['user.user_name'];
  gs.info(err.name);
  gs.info(err.message);
  gs.info(err.stack);
  gs.info("Error Processing this role: " + user);
  })(inputs, outputs);
}

(function paginate(variables, pageResponse) {
    // Use this script to set pagination variables or 
    // manipulate variables extracted by XPath or JSON Path

variables['offset'] = (parseInt(variables['offset']) + parseInt(variables['limit'])).toString();
variables['getNextPage'] = true;
if (variables['status'] == 'failure') {variables['getNextPage'] = false; }

})(variables, pageResponse);

