sys_variable_value.value:
(function execute(inputs, outputs) {
	if(inputs.status_code != '204'){
		var errorMsg = 'Error connecting to remote instance. Status code: ' + inputs.status_code;
		if(inputs.error_message)
			errorMsg = errorMsg + '. Message: ' + inputs.error_message;

      gs.info(errorMsg);
      //throw errorMsg;
   }
})(inputs, outputs);

